import React, { useEffect, useState } from 'react';
import api from '../utils/api';

interface Task {
  id: number;
  title: string;
  description: string;
  isComplete: boolean;
}

const TaskList = () => {
  const [tasks, setTasks] = useState<Task[]>([]);

  useEffect(() => {
    const fetchTasks = async () => {
      const { data } = await api.get('/tasks');
      setTasks(data);
    };
    fetchTasks();
  }, []);

  const handleComplete = async (taskId: number) => {
    await api.put(`/tasks/${taskId}`, { isComplete: true });
    setTasks((prevTasks) => prevTasks.map(task => task.id === taskId ? { ...task, isComplete: true } : task));
  };

  const handleDelete = async (taskId: number) => {
    await api.delete(`/tasks/${taskId}`);
    setTasks((prevTasks) => prevTasks.filter(task => task.id !== taskId));
  };

  return (
    <div>
      <h2>Tasks</h2>
      <ul>
        {tasks.map((task) => (
          <li key={task.id}>
            <h3>{task.title}</h3>
            <p>{task.isComplete ? 'Completed' : 'Not completed'}</p>
            <button onClick={() => handleComplete(task.id)}>Mark as Complete</button>
            <button onClick={() => handleDelete(task.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TaskList;
